import React, { useState } from 'react';
import axios from 'axios';

function UploadPage() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [savedInfo, setSavedInfo] = useState(null); // ‚úÖ Ï†ÄÏû• Í≤∞Í≥º ÏÉÅÌÉú

  // ÌååÏùº ÏÑ†ÌÉù Ïãú ÏÉÅÌÉú Ï†ÄÏû•
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  // ÏóÖÎ°úÎìú Î∞è Î∂ÑÏÑù ÏöîÏ≤≠ Ìï∏Îì§Îü¨
  const handleUpload = async () => {
    if (!file) {
      alert('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      // ‚úÖ Flask ÏÑúÎ≤ÑÎ°ú Î∂ÑÏÑù ÏöîÏ≤≠
      const res = await axios.post('http://localhost:5050/predict', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      console.log('‚úÖ AI Î∂ÑÏÑù Í≤∞Í≥º:', res.data);
      setResult(res.data);

      // ‚úÖ MongoDB Ï†ÄÏû• ÏöîÏ≤≠
      const userId = 'ÌÖåÏä§Ìä∏UserID123';
      const saveRes = await axios.post('http://localhost:5000/api/analysis/save', {
        userId,
        filename: file.name,
        result: res.data,
      });

      console.log('‚úÖ Í≤∞Í≥º Ï†ÄÏû• ÏôÑÎ£å:', saveRes.data);
      setSavedInfo(saveRes.data.data); // Ï†ÄÏû•Îêú Ìï≠Î™© Ï†ÑÏ≤¥ Ï†ÄÏû•

    } catch (err) {
      console.error('‚ùå Î∂ÑÏÑù Ïã§Ìå®:', err);
      alert('Î∂ÑÏÑù Ïã§Ìå®');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üìÅ PCAP ÌååÏùº ÏóÖÎ°úÎìú</h2>
      <input type="file" accept=".pcap" onChange={handleFileChange} />
      <button onClick={handleUpload} style={{ marginLeft: '1rem' }}>
        Î∂ÑÏÑù ÏöîÏ≤≠
      </button>

      {result && (
        <div style={{ marginTop: '2rem' }}>
          <h3>üìä Î∂ÑÏÑù Í≤∞Í≥º</h3>
          <p><strong>Í≥µÍ≤© Ïú†Ìòï:</strong> {result.attack}</p>
          <p><strong>ÏÑ§Î™Ö:</strong> {result.description}</p>
          <p><strong>ÏúÑÌóòÎèÑ:</strong> {result.risk_level}</p>
        </div>
      )}

      {savedInfo && (
        <div style={{ marginTop: '2rem', color: 'green' }}>
          ‚úÖ Ï†ÄÏû• ÏôÑÎ£åÎê® ({new Date(savedInfo.createdAt).toLocaleString('ko-KR')})
        </div>
      )}
    </div>
  );
}

export default UploadPage;
