import {
  ShieldAlert,
  Activity,
  Zap,
  RadioTower,
  Bug,
  Radar,
  Share2,
} from 'lucide-react'

function Attack() {
  const attacks = [
    {
      title: 'ICMP Flood',
      icon: <ShieldAlert className='text-red-500 w-6 h-6' />,
      desc: 'ICMP Echo Request를 대량 전송해 네트워크 자원과 시스템 성능을 저하시키는 DoS 공격입니다.',
    },
    {
      title: 'UDP Flood',
      icon: <Zap className='text-blue-500 w-6 h-6' />,
      desc: '대상 시스템에 무작위 UDP 패킷을 보내 ICMP 응답 생성으로 리소스를 소모시키는 공격입니다.',
    },
    {
      title: 'SYN Flood',
      icon: <Activity className='text-orange-500 w-6 h-6' />,
      desc: 'TCP 연결 요청을 무작위로 보내 서버의 연결 큐를 가득 채우는 자원 고갈 공격입니다.',
    },
    {
      title: 'Smurf Attack',
      icon: <RadioTower className='text-purple-500 w-6 h-6' />,
      desc: '브로드캐스트 주소에 ICMP 요청을 보내 다수의 응답이 피해자에게 집중되도록 유도하는 공격입니다.',
    },
    {
      title: 'Ping of Death',
      icon: <Bug className='text-pink-500 w-6 h-6' />,
      desc: '너무 큰 ping 패킷으로 시스템의 버퍼를 오버플로우시키는 오래된 DoS 공격입니다.',
    },
    {
      title: 'Port Scan',
      icon: <Radar className='text-green-500 w-6 h-6' />,
      desc: '공격 대상 시스템의 열려 있는 포트를 탐색하여 취약한 서비스를 찾기 위한 정찰 공격입니다.',
    },
    {
      title: 'ARP Spoofing',
      icon: <Share2 className='text-yellow-500 w-6 h-6' />,
      desc: '공격자가 MAC 주소를 위조해 ARP 테이블을 변조하고 트래픽을 가로채는 중간자 공격입니다.',
    },
  ]

  return (
    <div className='p-6 sm:p-10 bg-white min-h-screen'>
      <h2 className='text-3xl font-bold mb-10 text-center text-gray-800'>
        📚 보안 공격별 설명
      </h2>

      <div className='space-y-8'>
        {attacks.map((attack, index) => (
          <section key={index} className='border-b pb-4'>
            <div className='flex items-center gap-2 mb-2'>
              {attack.icon}
              <h3 className='text-xl font-semibold text-gray-900'>
                {index + 1}. {attack.title}
              </h3>
            </div>
            <p className='text-gray-700 text-sm'>{attack.desc}</p>
          </section>
        ))}
      </div>
    </div>
  )
}

export default Attack
