import {
  ShieldAlert,
  Activity,
  Zap,
  RadioTower,
  Bug,
  Radar,
  Share2,
} from 'lucide-react'

function Attack() {
  const attacks = [
    {
      title: 'ICMP Flood',
      icon: <ShieldAlert className='text-red-500 w-6 h-6' />,
      desc: 'ICMP Echo Request를 대량 전송해 네트워크 자원과 시스템 성능을 저하시키는 DoS 공격입니다.',
    },
    {
      title: 'UDP Flood',
      icon: <Zap className='text-blue-500 w-6 h-6' />,
      desc: '대상 시스템에 무작위 UDP 패킷을 보내 ICMP 응답 생성으로 리소스를 소모시키는 공격입니다.',
    },
    {
      title: 'SYN Flood',
      icon: <Activity className='text-orange-500 w-6 h-6' />,
      desc: 'TCP 연결 요청을 무작위로 보내 서버의 연결 큐를 가득 채우는 방식의 자원 고갈 공격입니다.',
    },
    {
      title: 'Smurf Attack',
      icon: <Broadcast className='text-purple-500 w-6 h-6' />,
      desc: '브로드캐스트 주소에 ICMP 요청을 보내 다수의 응답을 피해자에게 집중시키는 공격입니다.',
    },
    {
      title: 'Ping of Death',
      icon: <Bug className='text-pink-500 w-6 h-6' />,
      desc: '너무 큰 크기의 ping 패킷으로 시스템의 버퍼를 넘쳐서 충돌을 유발하는 오래된 공격입니다.',
    },
    {
      title: 'Port Scan',
      icon: <Radar className='text-green-500 w-6 h-6' />,
      desc: '시스템의 열린 포트를 탐색하여 취약한 서비스를 식별하려는 정찰 목적의 공격입니다.',
    },
    {
      title: 'ARP Spoofing',
      icon: <Share2 className='text-yellow-500 w-6 h-6' />,
      desc: '공격자가 MAC 주소를 위조해 ARP 테이블을 변조하고, 트래픽을 가로채는 중간자 공격입니다.',
    },
  ]

  return (
    <div className='p-6 sm:p-10 bg-gray-50 min-h-screen'>
      <h2 className='text-3xl font-bold mb-8 text-center text-gray-800'>
        📚 보안 공격별 설명
      </h2>
      <div className='grid gap-6 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3'>
        {attacks.map((attack, index) => (
          <div
            key={index}
            className='bg-white rounded-2xl shadow-md p-6 border border-gray-200 hover:shadow-lg transition'
          >
            <div className='flex items-center gap-3 mb-3'>
              {attack.icon}
              <h3 className='text-xl font-semibold text-gray-900'>
                {attack.title}
              </h3>
            </div>
            <p className='text-gray-600 text-sm'>{attack.desc}</p>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Attack
